import { Tree, utils } from 'phylocanvas';

const { getPixelRatio } = utils.canvas;

const DEFAULTS = {
  active: true,
};

function addListeners() {
  const tree = this
  const ctx = this.canvas;
  const canvas = ctx.canvas;
  const pixelRatio = getPixelRatio(ctx);
  const textSize = this.textSize
  if (this.treeStats.active) {
      //cleanup old event listeners
      for (let eventListener of this.eventListeners.mousemove.filter(eventListener => eventListener.listener.name == "treeStats")) {
        this.removeListener('mousemove', eventListener.listener, canvas)
      }
      console.log(this)
      for (let leaf of this.leaves) {
        this.addListener('mousemove', treeStats, canvas)
        function treeStats(e) {
          let path = new Path2D()
          let x = (tree.offsetx - tree.baseNodeSize * tree.zoom + leaf.getLabelStartX() * tree.zoom / pixelRatio + leaf.maxx * tree.zoom / pixelRatio) * pixelRatio
          let y = (tree.offsety + (leaf.maxy + leaf.miny) / 2 * tree.zoom / pixelRatio) * pixelRatio
          ctx.save()
          ctx.font = `${tree.textSize * pixelRatio}px ${tree.font}`;
          let width = ctx.measureText(leaf.label).width * pixelRatio * tree.zoom / tree.zoomFactor
          let height = ctx.measureText("M").width * pixelRatio * tree.zoom / tree.zoomFactor / 2
          ctx.restore()
          path.moveTo(x, y + height)
          path.lineTo(x + width, y + height)
          path.lineTo(x + width, y - height)
          path.lineTo(x, y - height)
          path.lineTo(x, y + height)
          path.closePath()
          ctx.stroke(path)
          if (ctx.isPointInPath(path, e.offsetX * pixelRatio, e.offsetY * pixelRatio)) {
            leaf.highlighted = true
            ctx.save()
            x = e.offsetX * pixelRatio
            y = e.offsetY * pixelRatio
            ctx.clearRect(x + 30, y + 10, 200, 100)
            ctx.fillRect(x + 10, y + 10, 200, 100)
            ctx.restore
            console.log(leaf)
          } else {
            leaf.highlighted = false
          }
        }
      }
  }
}

export default function plugin(decorate) {
  decorate(this, 'createTree', (delegate, args) => {
    const tree = delegate(...args);
    const [ , config = {} ] = args;
    tree.treeStats = Object.assign({}, DEFAULTS, config.treeStats || {});
    return tree;
  });
  decorate(Tree, 'load', function (delegate, args) {
    delegate.apply(this, args);
    addListeners.apply(this)
    let ctx = this.canvas
    this.addListener('mousemove', (e) => {
      let path = new Path2D()
      path.moveTo(0, 0)
      path.lineTo(e.offsetX * getPixelRatio(ctx), e.offsetY * getPixelRatio(ctx))
      path.closePath()
      ctx.stroke(path)
    })

  });
}
