import { Tree, utils } from 'phylocanvas';

const { getPixelRatio } = utils.canvas;

const DEFAULTS = {
  active: true,
  pairwiseMatrix: {},
};

function walkMatrix(start, node, walked, distance) {
  distance += node.branchLength
  distance = parseFloat(distance.toFixed(10))
  walked.push(node)

  if (node != start && node.children.length == 0) {
    console.log(start.id, node.id, distance)
    console.log(this)
    //pairwiseMatrix[leaf].push([node, distance])
  }

  for (let child of node.children) {
    if (! walked.includes(child)) {
      walkMatrix(start, child, walked, distance)
    }
  }
  if (node.parent && ! walked.includes(node.parent)) {
    walkMatrix(start, node.parent, walked, distance)
  }
}

function buildPairwiseMatrix() {
  console.log(this.leaves)
  for (let leaf in this.leaves) {
    this.pairwiseOps.pairwiseMatrix[leaf] = []
    walkMatrix(this.leaves[leaf], this.leaves[leaf], [], 0).apply(this)
  }
}

export default function plugin(decorate) {
  decorate(this, 'createTree', (delegate, args) => {
    const tree = delegate(...args);
    const [ , config = {} ] = args;
    tree.pairwiseOps = Object.assign({}, DEFAULTS, config.pairwiseOps || {});
    return tree;
  });
  decorate(Tree, 'load', function (delegate, args) {
    delegate.apply(this, args);
    if (this.pairwiseOps.active) {
      buildPairwiseMatrix.apply(this);
    }   
  });
}
