import React, {useEffect, useRef} from "react";
import Plotly from "plotly.js-dist";

function PlotlyView(props) {
  let Plotly = useRef();

  useEffect(() => {
    Plotly.current.newPlot('plotly', props.data)
  }, [])

  useEffect(() => {
    Plotly.current.restyle('plotly', props.data)
  }, [props.data])

  useEffect(() => {
    var data = [
      {
        x: ['data1', 'data2', 'data3'],
        y: [20, 14, 23],
        type: 'bar'
      }
    ];
    Plotly.newPlot('plotly', data);

    return () => {
    }
  }, [])

  return (
    <div id="plotly"></div>
  )
}

export default PlotlyView;
