import { Tree, utils } from 'phylocanvas';

const { getPixelRatio } = utils.canvas;

const DEFAULTS = {
  active: true,
  width: 100,
  height: 20,
  fillStyle: 'black',
  strokeStyle: 'black',
  lineWidth: 1,
  fontFamily: 'Sans-serif',
  fontSize: 16,
  textBaseline: 'bottom',
  textAlign: 'center',
  digits: 2,
  position: {
    bottom: 10,
    left: 10,
  },
};

const INVALID_HORIZONTAL_POSITION = 'Invalid horizontal position specified' +
  'Supported values are `left`, `centre`, or `right`';
const INVALID_VERTICAL_POSITION = 'Invalid vertical position specified' +
  'Supported values are `top`, `middle`, or `bottom`';

const LOG10 = Math.log(10);

function drawRoot() {

  const { root, zoom, branchScalar } = this;
  const { position } = root;
  const cxt = this.canvas;
  const canvas = cxt.canvas;
  const pixelRatio = getPixelRatio(cxt);

  cxt.save();

  cxt.fillStyle = root.fillStyle;
  cxt.strokeStyle = root.strokeStyle;
  cxt.lineWidth = this.lineWidth;
  console.log(this.root)

  let x = (this.offsetx + (this.root.centerx) * this.zoom / 2) * pixelRatio
  let y = (this.offsety + (this.root.centery) * this.zoom / 2) * pixelRatio

  if (this.treeType == "rectangular" || this.treeType == "diagonal") {
    cxt.beginPath();
    cxt.moveTo(x, y);
    cxt.lineTo(x-50, y);
    cxt.stroke();
    cxt.closePath();
  } else if (this.treeType == "hierarchical") {
    cxt.beginPath();
    cxt.moveTo(x, y);
    cxt.lineTo(x, y-50);
    cxt.stroke();
    cxt.closePath();
  }



  cxt.restore();
}

export default function plugin(decorate) {
  decorate(this, 'createTree', (delegate, args) => {
    const tree = delegate(...args);
    const [ , config = {} ] = args;
    tree.rootLine = Object.assign({}, DEFAULTS, config.rootLine || {});
    return tree;
  });
  decorate(Tree, 'draw', function (delegate, args) {
    delegate.apply(this, args);
    if (this.rootLine.active) {
      drawRoot.apply(this);
    }
  });
}
