import { Tree, utils } from 'phylocanvas';

const { getPixelRatio } = utils.canvas;

const DEFAULTS = {
  active: true,
};

function addListeners() {
  const cxt = this.canvas;
  const canvas = cxt.canvas;
  const pixelRatio = getPixelRatio(cxt);
  const textSize = this.textSize * this.zoom / 2 
  if (this.treeStats.active) {
      for (let leaf of this.leaves) {
        this.addListener('mousemove', (e) => {
          let path = new Path2D()
          let x = (this.offsetx + leaf.endx * this.zoom / 2) * pixelRatio
          let y = (this.offsety + leaf.endy * this.zoom / 2) * pixelRatio
          path.moveTo(x, y)
          path.lineTo(x + 10, y)
          path.lineTo(x + 10, y + 10)
          path.lineTo(x, y + 10)
          path.lineTo(x, y)
          path.closePath()
          if (this.canvas.isPointInPath(path, e.offsetX, e.offsetY)) {
            ctx.fill(path)
          }
        })
      }
  }
}

export default function plugin(decorate) {
  decorate(this, 'createTree', (delegate, args) => {
    const tree = delegate(...args);
    const [ , config = {} ] = args;
    tree.treeStats = Object.assign({}, DEFAULTS, config.treeStats || {});
    return tree;
  });
  decorate(Tree, 'load', function (delegate, args) {
    delegate.apply(this, args);
    addListeners.apply(this)    
  });
}
