import React, { useEffect, useState, useRef } from "react";

import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import SvgIcon from '@mui/material/SvgIcon';
import Chip from '@mui/material/Chip';

function SvgButton(props) {

  let svgList = useRef(
    {
      "blank": "",
      "menu": "M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z",
      "tree": "m5,3h14zv17z  m0,9h-5z  m6,8h-5zv-6zv3z m0,-6h12z m0,9h9z"
    }
  );

  let [svg, setSvg] = useState(svgList.current["blank"]);
  let [label, setLabel] = useState("");
  let [drop, setDrop] = useState(false);

  useEffect(() => {
    setSvg(svgList.current[props.svg])
    setLabel(props.label)
    setDrop(props.drop)
  }, [])

  if (label) {
    return (
      <p>
        Chip:&nbsp;
        <Chip
          size="small"
          label={label}
          onClick={() => {}}
          sx={{backgroundColor: "#ddd", boxShadow: "-1px 1px 2px", "&:hover": {boxShadow: "-2px 2px 3px", backgroundColor: "#eee", cursor: "pointer"}}}
          icon=<SvgIcon>
            <path stroke="black" d={svg}></path>
          </SvgIcon>
        />
      </p>
    )
  } else {
    return (
      <p>
        SvgButton:&nbsp;
        <IconButton size="small" onClick={() => {}} sx={{height: 24, width: 24, backgroundColor: "#ddd", boxShadow: "-1px 1px 2px", "&:hover": {boxShadow: "-2px 2px 3px", backgroundColor: "#eee"}}}>
          <SvgIcon sx={{height: 16, width:16}}>
            <g stroke="black" stroke-width="2" stroke-linecap="round" transform="rotate(90 12 12)">
              <path d={svg}></path>
            </g>
          </SvgIcon>
        </IconButton>
      </p>
    )
  }
}

export default SvgButton;
