import { Branch, utils } from 'phylocanvas';

const { getPixelRatio } = utils.canvas;

function drawStats() {
  console.log(this)
}

export default function plugin(decorate) {
  decorate(Branch, 'drawLabel', function (delegate, args) {
    delegate.apply(this, args);

        drawStats.apply(this);

  });
}
