import React, { useEffect, useState, useRef } from "react";
import Phylocanvas, { utils } from "phylocanvas";
import scalebar from "phylocanvas-plugin-scalebar";
import branchLength from "phylocanvas-plugin-branch-length";
import root from "phylocanvas-plugin-root";
import pairwiseOps from "phylocanvas-plugin-pairwise-ops";
import treeStats from "phylocanvas-plugin-tree-stats";
Phylocanvas.plugin(scalebar)
Phylocanvas.plugin(branchLength)
Phylocanvas.plugin(root)
Phylocanvas.plugin(pairwiseOps)
Phylocanvas.plugin(treeStats)

const { getPixelRatio } = utils.canvas;

function PhylocanvasView(props) {
  let phylocanvas = useRef();

  // height
  let [height, _setHeight] = useState(100)
  let minHeight = 100
  let heightRef = useRef(height)
  let setHeight = data => {
    heightRef.current = data
    _setHeight(data)
  }

  // width
  let [width, _setWidth] = useState(100)
  let minWidth = 100
  let widthRef = useRef(width)
  let setWidth = data => {
    widthRef.current = data
    _setWidth(data)
  }

  let typeList = useRef(["radial", "rectangular", "circular", "diagonal", "hierarchical"]);

  //TODO: Fix height inheritance problem (relative height to window size without adding to window size for positive feedback loop)

  useEffect(() => {
    function handleResize() {
      //setHeight(document.getElementsByClassName("Nav-body")[0].clientHeight)
      let heightChange = heightRef.current
      console.log(minHeight)
      setHeight(Math.min( Math.max( document.documentElement.clientHeight || 0, window.innerHeight || 0) - document.getElementsByClassName("Nav-header")[0].clientHeight * 2), minHeight )
      heightChange = heightChange/heightRef.current
      let widthChange = widthRef.current
      setWidth(Math.max( document.documentElement.clientWidth || minWidth, window.innerWidth || minWidth, minWidth))
      widthChange = widthChange/widthRef.current

      if (phylocanvas.current.treeType == "circular") {
        phylocanvas.current.offsety = phylocanvas.current.offsety / heightChange
        phylocanvas.current.offsetx = phylocanvas.current.offsetx / widthChange
        phylocanvas.current.zoom = phylocanvas.current.zoom / heightChange
      }
      if (phylocanvas.current.treeType == "rectangular") {
        phylocanvas.current.offsety = phylocanvas.current.offsety / heightChange
        phylocanvas.current.offsetx = phylocanvas.current.offsetx / widthChange
        phylocanvas.current.zoom = phylocanvas.current.zoom / heightChange
        phylocanvas.current.nodeSize = phylocanvas.current.nodeSize * heightChange
        phylocanvas.current.textSize = phylocanvas.current.textSize * heightChange
        phylocanvas.current.branchScalar = phylocanvas.current.branchScalar * heightChange
        phylocanvas.current.branchScalar = phylocanvas.current.branchScalar / widthChange
      }
      // scale horizontally
      //phylocanvas.current.zoom = phylocanvas.current.zoom / heightChange
      //phylocanvas.current.branchScalar = phylocanvas.current.branchScalar * heightChange
      //phylocanvas.current.offsetx = phylocanvas.current.offsetx / heightChange

      // scale vertically
      //phylocanvas.current.branchScalar = phylocanvas.current.branchScalar / widthChange
    } 
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, [])

  useEffect(() => {
    phylocanvas.current = Phylocanvas.createTree("phylocanvas")
  }, [])

  useEffect(() => {
    phylocanvas.current.load(props.tree)
  }, [props.tree])

  useEffect(() => {
    if (typeList.current.includes(props.type)) {
      phylocanvas.current.setTreeType(props.type)
    } else {
      phylocanvas.current.setTreeType("rectangular")
    }
    phylocanvas.current.setNodeSize(props.nodeSize * getPixelRatio(phylocanvas.current.canvas) / 2)
    phylocanvas.current.setTextSize(props.textSize * getPixelRatio(phylocanvas.current.canvas) / 2)
    phylocanvas.current.lineWidth = props.lineWidth * getPixelRatio(phylocanvas.current.canvas) / 2
  }, [props.type])

  useEffect(() => {
    phylocanvas.current.setNodeSize(props.nodeSize * getPixelRatio(phylocanvas.current.canvas) / 2)
  }, [props.nodeSize])

  useEffect(() => {
    phylocanvas.current.setTextSize(props.textSize * getPixelRatio(phylocanvas.current.canvas) / 2)
  }, [props.textSize])

  useEffect(() => {
    phylocanvas.current.showLabels = props.labels
    phylocanvas.current.alignLabels = props.align
    phylocanvas.current.lineWidth = props.lineWidth * getPixelRatio(phylocanvas.current.canvas) / 2
    phylocanvas.current.draw()
  }, [props.labels, props.align, props.lineWidth])

  useEffect(() => {
    phylocanvas.current.pairwiseOps.clusterDistance = props.clusterDistance
    phylocanvas.current.pairwiseOps.clusterSamples = props.clusterSamples
    console.log(phylocanvas.current)
    phylocanvas.current.pairwiseOps.clusterDraw = true
    phylocanvas.current.draw()
  }, [props.clusterDistance, props.clusterSamples])

  return (
    <div id="phylocanvas" style={{height: height + "px", width: "100%", minHeight: minHeight + "px", minWidth: minWidth + "px"}}></div>
  )
}

export default PhylocanvasView;
