import React, {useEffect, useRef} from "react";
import Plotly from "plotly.js-dist";

function PlotlyView(props) {
  let plotly = useRef();

  useEffect(() => {
    plotly.current = Plotly
    plotly.current.newPlot('plotly', props.data)
  }, [])


  useEffect(() => {
    plotly.current.restyle('plotly', props.data)
  }, [props.data])

  return (
    <div id="plotly"></div>
  )
}

export default PlotlyView;
